import React from 'react'
import { useState } from 'react';

export const Formularios = () => {

    const [formmulario, setFormmulario] = useState({
        email: 'test@test.com',
        password: '123456'
    })

    const onChange = ( value: string, campo: string ) => {
        setFormmulario({
            ...formmulario,
            [campo]: value
        })
    }

  return (
    <>
        <h3>Formularios</h3>
        <input 
            type="email" 
            name="" 
            id="" 
            className="form-control" 
            placeholder='Email' 
            value={ formmulario.email }
            onChange={ ( { target } ) => onChange(target.value , 'email')}
        />
        <input 
            type="text" 
            name="" 
            id="" 
            className="form-control mt-2 mb-2" 
            placeholder='Password'
            value={ formmulario.password}
            onChange={ ( { target } ) => onChange(target.value , 'password] ')}
        />

        <code>
            <pre>
                { JSON.stringify( formmulario, null)}
            </pre>
        </code>
        
    </>
  )
}
