import { useState } from 'react';
export const useForm = <T extends object>( formulario: T) => {
    const [formmulario, setFormmulario] = useState({
        email: 'test@test.com',
        password: '123456'
    })

    const onChange = ( value: string, campo: string ) => {
        setFormmulario({
            ...formmulario,
            [campo]: value
        })
    }

    return {
        ...formmulario,
        formmulario, 
        onChange
    }
}
