import { useState } from 'react';
export const useForm = <T extends object>( formulario: T) => {
    const [state, setState] = useState()

    const onChange = ( value: string, campo: string ) => {
        setState({
            ...formulario,
            [campo]: value
        })
    }

    return {
        ...state,
        state, 
        onChange
    }
}
